SELECT (Area.aareadesc)as 'Customer',   isnull((SELECT    RIGHT('' + CONVERT(varchar(100),FLOOR(sum(timetaken))),100)      +':'                             + RIGHT('00' + CONVERT(varchar(2),FLOOR((((sum(timetaken))-FLOOR((sum(timetaken))))*60))),2)      +':'      + RIGHT('00' + CONVERT(varchar(2),FLOOR((((sum(timetaken))-FLOOR((sum(timetaken))))*60)-FLOOR((((sum(timetaken))-FLOOR((sum(timetaken))))*60)))*60),2)),'0:00:00')as 'TimeTaken',                                                                                     isnull((SELECT    RIGHT('' + CONVERT(varchar(100),FLOOR(sum(TravelTime))),100)      +':'                             + RIGHT('00' + CONVERT(varchar(2),FLOOR((((sum(TravelTime))-FLOOR((sum(TravelTime))))*60))),2)      +':'      + RIGHT('00' + CONVERT(varchar(2),FLOOR((((sum(TravelTime))-FLOOR((sum(TravelTime))))*60)-FLOOR((((sum(TravelTime))-FLOOR((sum(TravelTime))))*60)))*60),2)),'0:00:00')as 'TravelTime'                                                                 FROM (ACTIONS INNER JOIN Faults ON ACTIONS.Faultid = Faults.Faultid) INNER JOIN Area ON Faults.Areaint = Area.Aarea  group by aareadesc   
