select aareadesc as 'Client Name',   sdesc as 'Site Name',   faultid as 'Request ID',  username as 'User',  uname as 'Technician',  datecleared as 'Date Closed',  fvalue as 'Satisfaction',  SatisfactionComment as 'Comments',  case       when FSurveyBool1=1 then 'Yes'    else 'No'   end as 'Checkbox 1',   case      when FSurveyBool2=1 then 'Yes'     else 'No'    end as 'Checkbox 2',      case                                     when FSurveyBool3=1 then 'Yes'       else 'No'                          end as 'Checkbox 3',                 case                                     when FSurveyBool4=1 then 'Yes'       else 'No'                          end as 'Checkbox 4',                 case                                     when FSurveyBool5=1 then 'Yes'       else 'No'                          end as 'Checkbox 5'                                                           from faults,area,site,uname,lookup     where satisfactionlevel>0               and aarea=areaint                      and ssitenum=sitenumber                and fdeleted=0                         and unum=assignedtoint                 and faults.satisfactionlevel=fcode and fid=8 
